webhooks.payload_template:
{
  "token": "{{secret.pushover_api_token}}",
  "user": "{{secret.pushover_api_user}}",
  "title": "Severity upgraded to {{case.severity}} on [{{case.case_number}}] {{case.title}} ({{case.case_type_name}})",
  "message": "An incident has been upgraded to severity {{case.severity}}. The lead investigator is {{case.lead_investigator}}. The case is currently {{case.status}} and in {{case.phase_name}} phase.",
  "data": {
    "priority": "{{case.severity|mapped}}"
  }
}

{
  "token": "{{secret.pushover_api_token}}",
  "user": "{{secret.pushover_api_user}}",
  "title": "New {{case.severity}} incident: [{{case.case_number}}] {{case.title}} ({{case.case_type_name}})",
  "message": "A new incident has been opened. The lead investigator is {{case.lead_investigator}}. The case is currently {{case.status}} and in {{case.phase_name}} phase.",
  "url_title": "Access the case here",
  "url": "{{meta.case_url}}",
  "data": {
    "priority": "{{case.severity|mapped}}"
  }
}

{
  "token": "{{secret.pushover_api_token}}",
  "user": "{{secret.pushover_api_user}}",
  "title": "You have been paged for {{case.case_number}} {{case.title}}",
  "message": "You have been paged by {{event.actor_username}} on case {{case.case_number}}:\n\n {{event.event_type|mapped}}: {{event.title}}, {{event.description}}.\n\nYou can access the incident here: {{meta.case_url}}"
}

{
  "token": "{{secret.pushover_api_token}}",
  "user": "{{secret.pushover_api_user}}",
  "title": "Timer {{timer.name}} at 50%",
  "message": "The timer {{timer.name}} is at 50%. There's {{timer.time_remaining_seconds}} seconds left until breach. ",
  "data": {
    "priority": "{{case.severity|mapped}}"
  }
}
